function telCheck() {
	var e = $("#tel_val").val();
	if ("" == e) return alert("手机号码不能为空!"), !1;
	var t = /(1[3-9]\d{9}$)/;
	if (!t.test(e)) return alert("请输入正确的手机号码！"), !1;
	var r = new Object;
	r.tel = $("#tel_val").val();
	var a = new Object,
		s = new Object;
	s.requestType = "sendRegisterSmsCode", s.loginUserId = "", s.macNo = "", s.appVersionCode = "", s.appLang = "", s.appChannel = "", s.appDeviceType = "", a.header = s, a.body = r;
	var l = JSON.stringify(a),
		n = encryptByDES(l, "1379246805559731246875312468");
	hashCode(n);
	$.ajax({
		type: "get",
		dataType: "jsonp",
		url: portalUrl + "/sms/sendRegisterSmsCode",
		jsonpCallback: "jsonpCallback",
		async: !1,
		data: {
			token: "",
			jsonParams: n,
			sign: hashCode(n),
			requestSource: "3"
		},
		success: function (e) {
			var t = e.retValue,
				r = trim(t, "g"),
				a = decryptByDES(r, "1379246805559731246875312468"),
				s = jQuery.parseJSON(a);
			alert(s.body.resultDesc)
		},
		error: function (e) {
			JSON.stringify(e)
		}
	})
}

function register() {
	if ("" == $("#tel_val").val()) return void alert("手机号码不能为空");
	if ("" == $("#yz_val").val()) return void alert("请输入验证码");
	if ("" == $('input[type="password"]').val()) return void alert("请输入密码");
	if ($('input[type="password"]').val().length < 6) return void alert("请输入至少6位数密码");
	var e = $('input[type="password"]').val(),
		t = new Object,
		r = new Object,
		a = new Object;
	a.tel = $("#tel_val").val(), a.smsCode = $("#yz_val").val(), a.inviteCode = $("#inviteCode").val(), a.pwd = $.md5(e), t.header = r, t.body = a;
	var s = JSON.stringify(t),
		l = encryptByDES(s, encryptKey);
	hashCode(l);
	$.ajax({
		type: "get",
		dataType: "jsonp",
		url: portalUrl + "/user/systemRegister",
		jsonpCallback: "jsonpCallback",
		async: !0,
		data: {
			token: "",
			jsonParams: l,
			sign: hashCode(l),
			requestSource: "3"
		},
		success: function (e) {
			var t = e.retValue,
				r = trim(t, "g"),
				a = decryptByDES(r, encryptKey),
				s = jQuery.parseJSON(a),
				l = s.body.resultCode,
				n = s.body.resultDesc;
			if ("1000" == l) {
				var o = s.body.data;
				JSON.stringify(o);
				sessionStorage.setItem("userinfo", o), setCookie("userinfo", o, 365), localStorage.setItem("userinfo", o), alert("注册成功!"), location.href = html5Url + "logo.html"
			} else alert(n)
		},
		error: function (e) {
			alert(t.body.resultDesc)
		}
	})
}